	"item": {
		"component": "Echo.Apps.TopicRadar.Dashboard.InstanceList.Instance"
	}
};

instances.templates.noApps =
	'<div class="{class:noApps}">{label:noAppsAvailable}</div>';

instances.init = function() {
	this.parent();
};

instances.renderers.newItem = function(element) {
	var self = this;

	var apps = this.config.get("apps", {});
	if ($.isEmptyObject(apps)) {
		return element.empty().append(this.substitute({"template": this.templates.noApps}));
	}
	var addingClassName = this.substitute({"template": "{inherited.class:newItemAdding} {class:newItemAdding}"});
	element.empty();
	var dropdown = new Echo.GUI.Dropdown({
		"target": element,
		"title": this.labels.get("addNewItem"),
		"entries": $.map(apps, function(app) {
			return {
				"title": app.title,
				"handler": function() {
					element.addClass(addingClassName);
					dropdown.setTitle(self.labels.get("adding"));
					self.addItem({
						"id": Echo.Utils.getUniqueString(),
						"app": app
					}, function() {
						element.removeClass(addingClassName);
						dropdown.setTitle(self.labels.get("addnewItem"));
					});
				}
			};
		})
	});
	return element;
};

instances.methods._initItem = function(data, callback) {
	var meta = this.config.get("meta", {});
	var apps = this.config.get("apps", {});

	var instanceMeta = meta[data.id] || {};
	data.app = data.app || apps[instanceMeta.appId];
	data.title = data.app && data.app.title;

	data = $.extend(true, {}, data);
	return this.parent(data, callback);
};

instances.methods._prepareAppsList = function(subscriptions) {
	var self = this;
	subscriptions = subscriptions || [];
	return Echo.Utils.foldl([], subscriptions, function(subscription, acc) {
		if (!subscription.app) return;

		var dashboard = self._getInstancesDashboard(subscription.app.dashboards);
		var endpoints = subscription.app.endpoints || {};
		if (
			dashboard
			&& !endpoints["instance/add"]
			&& !endpoints["instance/remove"]
		) {
			acc.push({
				"app": subscription.app,
				"dashboard": dashboard
			});
		}
	});
};

instances.methods._getInstancesDashboard = function(dashboards) {
	return $.grep(dashboards || [], function(dashboard) {
		return dashboard.type === "instances";
	})[0];
};

instances.css =
	// common styles
	'.{class:noApps} { color: #a90000; }' +

	// remove 'plus' icon from newItem container and insert it into <a> tag.
	// in this case this icon will be clickable.
	'.{class:newItem} { background: none; padding-left: 0px; }' +
	'.{class:newItem}.{class:newItemAdding} { background-image: none; }' +
	'.{class:newItem} a.dropdown-toggle { background: left center no-repeat url({config:cdnBaseURL.apps.appserver}/images/plus.png); padding-left: 23px; }' +
	'.{class:newItem}.{class:newItemAdding} a.dropdown-toggle { background-image: url({config:cdnBaseURL.sdk-assets}/images/loading.gif);}' +

	// dropdown styles
	'.echo-sdk-ui .{class:newItem} .dropdown-toggle,' +
	'.echo-sdk-ui .{class:newItem} .open .dropdown-toggle:focus,' +
	'.echo-sdk-ui .{class:newItem} .open .dropdown-toggle:active,' +
	'.echo-sdk-ui .{class:newItem} .open .dropdown-toggle:link' +
	'  { font: 12px Arial; font-weight: bold; color: #787878; text-decoration: none; }' +
	'.echo-sdk-ui .{class:newItem} > ul { margin-left: 0px; }';

Echo.AppServer.Dashboard.create(instances);

})(Echo.jQuery);

package org.apache.cassandra.net;

import java.util.EnumSet;

import org.slf4j.Logger;
import org.slf4j.LoggerFactory;

import org.apache.cassandra.gms.Gossiper;

public class MessageDeliveryTask implements Runnable
{
    private static final Logger logger = LoggerFactory.getLogger(MessageDeliveryTask.class);

    private final MessageIn message;
    private final int id;
    private final long constructionTime;
    private final boolean isCrossNodeTimestamp;

    public MessageDeliveryTask(MessageIn message, int id, long timestamp, boolean isCrossNodeTimestamp)
    {
        assert message != null;
        this.message = message;
        this.id = id;
        this.constructionTime = timestamp;
        this.isCrossNodeTimestamp = isCrossNodeTimestamp;
    }

    public void run()
    {
        MessagingService.Verb verb = message.verb;
        if (MessagingService.DROPPABLE_VERBS.contains(verb)
            && System.currentTimeMillis() > constructionTime + message.getTimeout())
        {
            MessagingService.instance().incrementDroppedMessages(verb, isCrossNodeTimestamp);
            return;
        }

        IVerbHandler verbHandler = MessagingService.instance().getVerbHandler(verb);
        if (verbHandler == null)
        {
            logger.debug("Unknown verb {}", verb);
            return;
        }

        try
        {
            verbHandler.doVerb(message, id);
        }
        catch (Throwable t)
        {
            if (message.doCallbackOnFailure())
            {
                MessageOut response = new MessageOut(MessagingService.Verb.INTERNAL_RESPONSE)
                                                    .withParameter(MessagingService.FAILURE_RESPONSE_PARAM, MessagingService.ONE_BYTE);
                MessagingService.instance().sendReply(response, id, message.from);
            }

            throw t;
        }
        if (GOSSIP_VERBS.contains(message.verb))
            Gossiper.instance.setLastProcessedMessageAt(constructionTime);
    }

    private static final EnumSet<MessagingService.Verb> GOSSIP_VERBS = EnumSet.of(MessagingService.Verb.GOSSIP_DIGEST_ACK,
                                                             MessagingService.Verb.GOSSIP_DIGEST_ACK2,
                                                             MessagingService.Verb.GOSSIP_DIGEST_SYN);
}

package com.google.common.collect;

import com.google.common.annotations.Beta;
import com.google.common.annotations.GwtCompatible;
import com.google.common.base.Objects;
import com.google.errorprone.annotations.CanIgnoreReturnValue;
import java.util.Collection;
import java.util.Iterator;
import java.util.Map;
import java.util.Set;
import org.checkerframework.checker.nullness.compatqual.NullableDecl;

/**
 * A map which forwards all its method calls to another map. Subclasses should override one or more
 * methods to modify the behavior of the backing map as desired per the <a
 * href="http://en.wikipedia.org/wiki/Decorator_pattern">decorator pattern</a>.
 *
 * <p><b>Warning:</b> The methods of {@code ForwardingMap} forward <i>indiscriminately</i> to the
 * methods of the delegate. For example, overriding {@link #put} alone <i>will not</i> change the
 * behavior of {@link #putAll}, which can lead to unexpected behavior. In this case, you should
 * override {@code putAll} as well, either providing your own implementation, or delegating to the
 * provided {@code standardPutAll} method.
 *
 * <p><b>{@code default} method warning:</b> This class does <i>not</i> forward calls to {@code
 * default} methods. Instead, it inherits their default implementations. When those implementations
 * invoke methods, they invoke methods on the {@code ForwardingMap}.
 *
 * <p>Each of the {@code standard} methods, where appropriate, use {@link Objects#equal} to test
 * equality for both keys and values. This may not be the desired behavior for map implementations
 * that use non-standard notions of key equality, such as a {@code SortedMap} whose comparator is
 * not consistent with {@code equals}.
 *
 * <p>The {@code standard} methods and the collection views they return are not guaranteed to be
 * thread-safe, even when all of the methods that they depend on are thread-safe.
 *
 * @author Kevin Bourrillion
 * @author Jared Levy
 * @author Louis Wasserman
 * @since 2.0
 */
@GwtCompatible
public abstract class ForwardingMap<K, V> extends ForwardingObject implements Map<K, V> {
  // TODO(lowasser): identify places where thread safety is actually lost

  /** Constructor for use by subclasses. */
  protected ForwardingMap() {}

  @Override
  protected abstract Map<K, V> delegate();

  @Override
  public int size() {
    return delegate().size();
  }

  @Override
  public boolean isEmpty() {
    return delegate().isEmpty();
  }

  @CanIgnoreReturnValue
  @Override
  public V remove(Object key) {
    return delegate().remove(key);
  }

  @Override
  public void clear() {
    delegate().clear();
  }

  @Override
  public boolean containsKey(@NullableDecl Object key) {
    return delegate().containsKey(key);
  }

  @Override
  public boolean containsValue(@NullableDecl Object value) {
    return delegate().containsValue(value);
  }

  @Override
  public V get(@NullableDecl Object key) {
    return delegate().get(key);
  }

  @CanIgnoreReturnValue
  @Override
  public V put(K key, V value) {
    return delegate().put(key, value);
  }

  @Override
  public void putAll(Map<? extends K, ? extends V> map) {
    delegate().putAll(map);
  }

  @Override
  public Set<K> keySet() {
    return delegate().keySet();
  }

  @Override
  public Collection<V> values() {
    return delegate().values();
  }

  @Override
  public Set<Entry<K, V>> entrySet() {
    return delegate().entrySet();
  }

  @Override
  public boolean equals(@NullableDecl Object object) {
    return object == this || delegate().equals(object);
  }

  @Override
  public int hashCode() {
    return delegate().hashCode();
  }

  /**
   * A sensible definition of {@link #putAll(Map)} in terms of {@link #put(Object, Object)}. If you
   * override {@link #put(Object, Object)}, you may wish to override {@link #putAll(Map)} to forward
   * to this implementation.
   *
   * @since 7.0
   */
  protected void standardPutAll(Map<? extends K, ? extends V> map) {
    Maps.putAllImpl(this, map);
  }

  /**
   * A sensible, albeit inefficient, definition of {@link #remove} in terms of the {@code iterator}
   * method of {@link #entrySet}. If you override {@link #entrySet}, you may wish to override {@link
   * #remove} to forward to this implementation.
   *
   * <p>Alternately, you may wish to override {@link #remove} with {@code keySet().remove}, assuming
   * that approach would not lead to an infinite loop.
   *
   * @since 7.0
   */
  @Beta
  protected V standardRemove(@NullableDecl Object key) {
    Iterator<Entry<K, V>> entryIterator = entrySet().iterator();
    while (entryIterator.hasNext()) {
      Entry<K, V> entry = entryIterator.next();
      if (Objects.equal(entry.getKey(), key)) {
        V value = entry.getValue();
        entryIterator.remove();
        return value;
      }
    }
    return null;
  }

  /**
   * A sensible definition of {@link #clear} in terms of the {@code iterator} method of {@link
   * #entrySet}. In many cases, you may wish to override {@link #clear} to forward to this
   * implementation.
   *
   * @since 7.0
   */
  protected void standardClear() {
    Iterators.clear(entrySet().iterator());
  }

  /**
   * A sensible implementation of {@link Map#keySet} in terms of the following methods: {@link
   * ForwardingMap#clear}, {@link ForwardingMap#containsKey}, {@link ForwardingMap#isEmpty}, {@link
   * ForwardingMap#remove}, {@link ForwardingMap#size}, and the {@link Set#iterator} method of
   * {@link ForwardingMap#entrySet}. In many cases, you may wish to override {@link
   * ForwardingMap#keySet} to forward to this implementation or a subclass thereof.
   *
   * @since 10.0
   */
  @Beta
  protected class StandardKeySet extends Maps.KeySet<K, V> {
    /** Constructor for use by subclasses. */
    public StandardKeySet() {
      super(ForwardingMap.this);
    }
  }

  /**
   * A sensible, albeit inefficient, definition of {@link #containsKey} in terms of the {@code
   * iterator} method of {@link #entrySet}. If you override {@link #entrySet}, you may wish to
   * override {@link #containsKey} to forward to this implementation.
   *
   * @since 7.0
   */
  @Beta
  protected boolean standardContainsKey(@NullableDecl Object key) {
    return Maps.containsKeyImpl(this, key);
  }

  /**
   * A sensible implementation of {@link Map#values} in terms of the following methods: {@link
   * ForwardingMap#clear}, {@link ForwardingMap#containsValue}, {@link ForwardingMap#isEmpty},
   * {@link ForwardingMap#size}, and the {@link Set#iterator} method of {@link
   * ForwardingMap#entrySet}. In many cases, you may wish to override {@link ForwardingMap#values}
   * to forward to this implementation or a subclass thereof.
   *
   * @since 10.0
   */
  @Beta
  protected class StandardValues extends Maps.Values<K, V> {
    /** Constructor for use by subclasses. */
    public StandardValues() {
      super(ForwardingMap.this);
    }
  }

  /**
   * A sensible definition of {@link #containsValue} in terms of the {@code iterator} method of
   * {@link #entrySet}. If you override {@link #entrySet}, you may wish to override {@link
   * #containsValue} to forward to this implementation.
   *
   * @since 7.0
   */
  protected boolean standardContainsValue(@NullableDecl Object value) {
    return Maps.containsValueImpl(this, value);
  }

  /**
   * A sensible implementation of {@link Map#entrySet} in terms of the following methods: {@link
   * ForwardingMap#clear}, {@link ForwardingMap#containsKey}, {@link ForwardingMap#get}, {@link
   * ForwardingMap#isEmpty}, {@link ForwardingMap#remove}, and {@link ForwardingMap#size}. In many
   * cases, you may wish to override {@link #entrySet} to forward to this implementation or a
   * subclass thereof.
   *
   * @since 10.0
   */
  @Beta
  protected abstract class StandardEntrySet extends Maps.EntrySet<K, V> {
    /** Constructor for use by subclasses. */
    public StandardEntrySet() {}

    @Override
    Map<K, V> map() {
      return ForwardingMap.this;
    }
  }

  /**
   * A sensible definition of {@link #isEmpty} in terms of the {@code iterator} method of {@link
   * #entrySet}. If you override {@link #entrySet}, you may wish to override {@link #isEmpty} to
   * forward to this implementation.
   *
   * @since 7.0
   */
  protected boolean standardIsEmpty() {
    return !entrySet().iterator().hasNext();
  }

  /**
   * A sensible definition of {@link #equals} in terms of the {@code equals} method of {@link
   * #entrySet}. If you override {@link #entrySet}, you may wish to override {@link #equals} to
   * forward to this implementation.
   *
   * @since 7.0
   */
  protected boolean standardEquals(@NullableDecl Object object) {
    return Maps.equalsImpl(this, object);
  }

  /**
   * A sensible definition of {@link #hashCode} in terms of the {@code iterator} method of {@link
   * #entrySet}. If you override {@link #entrySet}, you may wish to override {@link #hashCode} to
   * forward to this implementation.
   *
   * @since 7.0
   */
  protected int standardHashCode() {
    return Sets.hashCodeImpl(entrySet());
  }

  /**
   * A sensible definition of {@link #toString} in terms of the {@code iterator} method of {@link
   * #entrySet}. If you override {@link #entrySet}, you may wish to override {@link #toString} to
   * forward to this implementation.
   *
   * @since 7.0
   */
  protected String standardToString() {
    return Maps.toStringImpl(this);
  }
}

package info.gehrels.flockDBClient;

import com.twitter.flockdb.thrift.SelectOperation;
import com.twitter.flockdb.thrift.SelectOperationType;
import com.twitter.flockdb.thrift.SelectQuery;
import org.hamcrest.Description;
import org.hamcrest.FeatureMatcher;
import org.hamcrest.Matcher;
import org.hamcrest.TypeSafeDiagnosingMatcher;

import java.util.List;

import static info.gehrels.flockDBClient.ByteHelper.toLongArray;
import static info.gehrels.flockDBClient.PrimitiveMatchers.isLongArray;
import static org.hamcrest.CoreMatchers.is;
import static org.hamcrest.Matchers.equalTo;

class SelectMatchers {
	static TypeSafeDiagnosingMatcher<SelectOperation> aSelectOperation(
		final TypeSafeDiagnosingMatcher<SelectOperation>... subMatchers) {
		return new TypeSafeDiagnosingMatcher<SelectOperation>() {
			@Override
			protected boolean matchesSafely(SelectOperation selectOperation, Description mismatchDescription) {
				for (TypeSafeDiagnosingMatcher<SelectOperation> matcher : subMatchers) {
					if (!matcher.matches(selectOperation)) {
						mismatchDescription.appendText("a SelectOperation[");
						matcher.describeMismatch(selectOperation, mismatchDescription);
						mismatchDescription.appendText("]");
						return false;
					}
				}
				return true;
			}

			@Override
			public void describeTo(Description description) {
				description.appendText("a SelectOperation[");
				boolean first = true;
				for (TypeSafeDiagnosingMatcher<SelectOperation> subMatcher : subMatchers) {
					if (first) {
						first = false;
					} else {
						description.appendText(", ");
					}

					subMatcher.describeTo(description);
				}
				description.appendText("]");
			}
		};
	}

	static FeatureMatcher<SelectOperation, SelectOperationType> withType(SelectOperationType operationType) {
		return new FeatureMatcher<SelectOperation, SelectOperationType>(is(operationType), "operation type",
		                                                                "operation type") {
			@Override
			protected SelectOperationType featureValueOf(SelectOperation actual) {
				return actual.getOperation_type();
			}
		};
	}

	static FeatureMatcher<SelectOperation, Long> withSourceId(long sourceId) {
		return new FeatureMatcher<SelectOperation, Long>(is(sourceId), "source id", "source id") {
			@Override
			protected Long featureValueOf(SelectOperation selectOperation) {
				return selectOperation.getTerm().getSource_id();
			}
		};
	}

	static FeatureMatcher<SelectOperation, Integer> withGraphId(int graphId) {
		return new FeatureMatcher<SelectOperation, Integer>(is(graphId), "graph id", "graph id") {
			@Override
			protected Integer featureValueOf(SelectOperation selectOperation) {
				return selectOperation.getTerm().getGraph_id();
			}
		};
	}

	static FeatureMatcher<SelectOperation, Boolean> withForward(boolean isForward) {
		return new FeatureMatcher<SelectOperation, Boolean>(is(isForward), "forward", "forward") {
			@Override
			protected Boolean featureValueOf(SelectOperation selectOperation) {
				return selectOperation.getTerm().isIs_forward();
			}
		};
	}

	static FeatureMatcher<SelectQuery, List<SelectOperation>> withOperations(
		Matcher<Iterable<? extends SelectOperation>> subMatcher) {
		return new FeatureMatcher<SelectQuery, List<SelectOperation>>(subMatcher, "selectOperations:",
		                                                              "selectOperations:") {
			@Override
			protected List<SelectOperation> featureValueOf(SelectQuery selectQuery) {
				return selectQuery.getOperations();
			}
		};
	}

	static Matcher<SelectQuery> aSelectQuery(final TypeSafeDiagnosingMatcher<SelectQuery>... subMatchers) {
		return new TypeSafeDiagnosingMatcher<SelectQuery>() {
			@Override
			protected boolean matchesSafely(SelectQuery selectQuery, Description mismatchDescription) {
				for (TypeSafeDiagnosingMatcher<SelectQuery> subMatcher : subMatchers) {
					if (!subMatcher.matches(selectQuery)) {
						mismatchDescription.appendText("a SelectQuery[");
						subMatcher.describeMismatch(selectQuery, mismatchDescription);
						mismatchDescription.appendText("]");
						return false;
					}
				}


				return true;
			}

			@Override
			public void describeTo(Description description) {
				description.appendText("a SelectQuery[");
				boolean first = true;
				for (TypeSafeDiagnosingMatcher<SelectQuery> subMatcher : subMatchers) {
					if (first) {
						first = false;
					} else {
						description.appendText(", ");
					}
					description.appendDescriptionOf(subMatcher);
				}

				description.appendText("]");
			}
		};
	}

	static FeatureMatcher<SelectOperation, long[]> withDestinationIds(long... destinationIds) {
		return new FeatureMatcher<SelectOperation, long[]>(isLongArray(destinationIds), "destination ids:", "destination ids:") {
			@Override
			protected long[] featureValueOf(SelectOperation actual) {
				return toLongArray(actual.getTerm().getDestination_ids());
			}
		};
	}

	static TypeSafeDiagnosingMatcher<SelectOperation> withoutQueryTerm() {
		return new TypeSafeDiagnosingMatcher<SelectOperation>() {
			@Override
			protected boolean matchesSafely(SelectOperation item, Description mismatchDescription) {
				if (item.isSetTerm()) {
					mismatchDescription.appendText("queryTerm was set");
					return false;
				}

				return true;
			}

			@Override
			public void describeTo(Description description) {
				description.appendText("without a query Term");
			}
		};
	}

	static FeatureMatcher<SelectQuery, Long> withCursor(long startIndex) {
		return new FeatureMatcher<SelectQuery, Long>(equalTo(startIndex), "start index", "start index") {
			@Override
			protected Long featureValueOf(SelectQuery actual) {
				return actual.getPage().getCursor();
			}
		};
	}

	static FeatureMatcher<SelectQuery, Integer> withMaxResults(int maxResults) {
		return new FeatureMatcher<SelectQuery, Integer>(equalTo(maxResults), "max. results", "max. results") {
			@Override
			protected Integer featureValueOf(SelectQuery actual) {
				return actual.getPage().getCount();
			}
		};
	}
}

#include "config.h"
#include "HTMLParserIdioms.h"

#include "Decimal.h"
#include <limits>
#include <wtf/MathExtras.h>
#include <wtf/text/AtomicString.h>
#include <wtf/text/StringBuilder.h>

namespace WebCore {

template <typename CharType>
static String stripLeadingAndTrailingHTMLSpaces(String string, CharType characters, unsigned length)
{
    unsigned numLeadingSpaces = 0;
    unsigned numTrailingSpaces = 0;

    for (; numLeadingSpaces < length; ++numLeadingSpaces) {
        if (isNotHTMLSpace(characters[numLeadingSpaces]))
            break;
    }

    if (numLeadingSpaces == length)
        return string.isNull() ? string : emptyAtom.string();

    for (; numTrailingSpaces < length; ++numTrailingSpaces) {
        if (isNotHTMLSpace(characters[length - numTrailingSpaces - 1]))
            break;
    }

    ASSERT(numLeadingSpaces + numTrailingSpaces < length);

    if (!(numLeadingSpaces | numTrailingSpaces))
        return string;

    return string.substring(numLeadingSpaces, length - (numLeadingSpaces + numTrailingSpaces));
}

String stripLeadingAndTrailingHTMLSpaces(const String& string)
{
    unsigned length = string.length();

    if (!length)
        return string.isNull() ? string : emptyAtom.string();

    if (string.is8Bit())
        return stripLeadingAndTrailingHTMLSpaces(string, string.characters8(), length);

    return stripLeadingAndTrailingHTMLSpaces(string, string.characters(), length);
}

String serializeForNumberType(const Decimal& number)
{
    if (number.isZero()) {
        // Decimal::toString appends exponent, e.g. "0e-18"
        return number.isNegative() ? "-0" : "0";
    }
    return number.toString();
}

String serializeForNumberType(double number)
{
    // According to HTML5, "the best representation of the number n as a floating
    // point number" is a string produced by applying ToString() to n.
    return String::numberToStringECMAScript(number);
}

Decimal parseToDecimalForNumberType(const String& string, const Decimal& fallbackValue)
{
    // See HTML5 2.5.4.3 `Real numbers.' and parseToDoubleForNumberType

    // String::toDouble() accepts leading + and whitespace characters, which are not valid here.
    const UChar firstCharacter = string[0];
    if (firstCharacter != '-' && firstCharacter != '.' && !isASCIIDigit(firstCharacter))
        return fallbackValue;

    const Decimal value = Decimal::fromString(string);
    if (!value.isFinite())
        return fallbackValue;

    // Numbers are considered finite IEEE 754 single-precision floating point values.
    // See HTML5 2.5.4.3 `Real numbers.'
    // FIXME: We should use numeric_limits<double>::max for number input type.
    const Decimal floatMax = Decimal::fromDouble(std::numeric_limits<float>::max());
    if (value < -floatMax || value > floatMax)
        return fallbackValue;

    // We return +0 for -0 case.
    return value.isZero() ? Decimal(0) : value;
}

Decimal parseToDecimalForNumberType(const String& string)
{
    return parseToDecimalForNumberType(string, Decimal::nan());
}

double parseToDoubleForNumberType(const String& string, double fallbackValue)
{
    // See HTML5 2.5.4.3 `Real numbers.'

    // String::toDouble() accepts leading + and whitespace characters, which are not valid here.
    UChar firstCharacter = string[0];
    if (firstCharacter != '-' && firstCharacter != '.' && !isASCIIDigit(firstCharacter))
        return fallbackValue;

    bool valid = false;
    double value = string.toDouble(&valid);
    if (!valid)
        return fallbackValue;

    // NaN and infinity are considered valid by String::toDouble, but not valid here.
    if (!isfinite(value))
        return fallbackValue;

    // Numbers are considered finite IEEE 754 single-precision floating point values.
    // See HTML5 2.5.4.3 `Real numbers.'
    if (-std::numeric_limits<float>::max() > value || value > std::numeric_limits<float>::max())
        return fallbackValue;

    // The following expression converts -0 to +0.
    return value ? value : 0;
}

double parseToDoubleForNumberType(const String& string)
{
    return parseToDoubleForNumberType(string, std::numeric_limits<double>::quiet_NaN());
}

template <typename CharacterType>
static bool parseHTMLIntegerInternal(const CharacterType* position, const CharacterType* end, int& value)
{
    // Step 3
    int sign = 1;

    // Step 4
    while (position < end) {
        if (!isHTMLSpace(*position))
            break;
        ++position;
    }

    // Step 5
    if (position == end)
        return false;
    ASSERT(position < end);

    // Step 6
    if (*position == '-') {
        sign = -1;
        ++position;
