describeWithRealConnection('CPUProfileView test', () => {
  it('reads registered console profile messages from the model', async () => {
    const Profiler = await import('../../../../../front_end/panels/profiler/profiler.js');
    const target = createTarget();
    const model = target.model(SDK.CPUProfilerModel.CPUProfilerModel) as SDK.CPUProfilerModel.CPUProfilerModel;
    const scriptId = 'bar' as Protocol.Runtime.ScriptId;
    const lineNumber = 42;
    const cpuProfile = {
      nodes: [{
        id: 1,
        callFrame: {functionName: 'fun', scriptId, lineNumber, url: 'http://foo', columnNumber: 1},
        hitCount: 42,
      }],
      startTime: 1,
      endTime: 2,
    };
    model.consoleProfileFinished({
      id: 'foo',
      location: {scriptId, lineNumber},
      profile: cpuProfile,
    });
    const profileType = new Profiler.CPUProfileView.CPUProfileType();
    const cpuProfileHeader = profileType.getProfiles()[0] as Profiler.CPUProfileView.CPUProfileHeader;
    assert.deepEqual(cpuProfileHeader?.cpuProfilerModel?.registeredConsoleProfileMessages[0]?.cpuProfile, cpuProfile);
  });
});

extern crate ansi_term;
extern crate version_check;

use ansi_term::Colour::{Red, Yellow, Blue, White};
use version_check::{is_nightly, is_min_version};

// Specifies the minimum nightly version needed to compile Rocket.
const MIN_VERSION: &'static str = "1.16.0-nightly";

// Convenience macro for writing to stderr.
macro_rules! printerr {
    ($($arg:tt)*) => ({
        use std::io::prelude::*;
        write!(&mut ::std::io::stderr(), "{}\n", format_args!($($arg)*))
            .expect("Failed to write to stderr.")
    })
}

fn main() {
    let (ok_nightly, ok_version) = (is_nightly(), is_min_version(MIN_VERSION));
    let print_version_err = |version: &str| {
        printerr!("{} {}. {} {}.",
                  White.paint("Installed version is:"),
                  Yellow.paint(version),
                  White.paint("Minimum required:"),
                  Yellow.paint(MIN_VERSION));
    };

    if let (Some(is_nightly), Some((ok_version, version))) = (ok_nightly, ok_version) {
        if !is_nightly {
            printerr!("{} {}",
                      Red.bold().paint("Error:"),
                      White.paint("Rocket requires a nightly version of Rust."));
            print_version_err(&*version);
            printerr!("{}{}{}",
                      Blue.paint("See the getting started guide ("),
                      White.paint("https://rocket.rs/guide/getting-started/"),
                      Blue.paint(") for more information."));
            panic!("Aborting compilation due to incompatible compiler.")
        }

        if !ok_version {
            printerr!("{} {}",
                      Red.bold().paint("Error:"),
                      White.paint("Rocket requires a newer version of rustc."));
            printerr!("{}{}{}",
                      Blue.paint("Use `"),
                      White.paint("rustup update"),
                      Blue.paint("` or your preferred method to update Rust."));
            print_version_err(&*version);
            panic!("Aborting compilation due to incompatible compiler.")
        }
    } else {
        println!("cargo:warning={}", "Rocket was unable to check rustc compatibility.");
        println!("cargo:warning={}", "Build may fail due to incompatible rustc version.");
    }
}

This is the basic way of creating a JPEG from a PDF-File

```
<?php
use Org_Heigl\Ghostscript\Ghostscript;

// Create the Ghostscript-Wrapper
$gs = new Ghostscript ();

// Set the output-device
$gs->setDevice('jpeg')
// Set the input file
   ->setInputFile('path/to/my/ps/or/pdf/file')
// Set the output file that will be created in the same directory as the input
   ->setOutputFile('output')
// Set the resolution to 96 pixel per inch
   ->setResolution(96)
// Set Text-antialiasing to the highest level
   ->setTextAntiAliasing(Ghostscript::ANTIALIASING_HIGH);
// Set the jpeg-quality to 100 (This is device-dependent!)
   ->getDevice()->setQuality(100);
// convert the input file to an image
if (true === $gs->render()) {
    echo 'success';
} else {
    echo 'some error occured';
}
```

