package com.twu.biblioteca.service;

import com.twu.biblioteca.model.Movie;
import com.twu.biblioteca.util.UserInput;

import java.util.ArrayList;
import java.util.List;

public class MovieService {

    private static List<Movie> availableMovies;
    private static List<Movie> checkedOutMovies;

    public static void populateMovies() {
        availableMovies = new ArrayList<Movie>();
        checkedOutMovies = new ArrayList<Movie>();
        Movie movie = new Movie("The Hunger Games", "2012", "Gary Ross", 7);
        availableMovies.add(movie);
    }

    public void listAvailableMovies() {
        for (Movie m : availableMovies) {
            m.print();
        }
    }

    public Movie checkoutMovie(Movie movie) {
        if (availableMovies.contains(movie)) {
            availableMovies.remove(movie);
            checkedOutMovies.add(movie);
            return movie;
        }
        return null;
    }

    public Movie obtainMovie() {
        String movieName = UserInput.obtainInput("Write the movie name: ");
        return findBookByName(movieName, availableMovies);
    }

    private Movie findBookByName(String name, List<Movie> movies) {
        for (Movie movie : movies) {
            if (movie.getName().equals(name)) {
                return movie;
            }
        }
        return null;
    }
}

const { readdir, readFileSync, writeFileSync } = require('fs');
const { info } = require('better-console');

const { PROBLEMS_PATH, README_BASE_PATH, README_PATH, DIFFICULTY_MAP, getQuestionsDetails, formatId, questionUrl } = require('./script-utils');

const getSolvedQuestionIds = () => new Promise(resolve => {
  readdir(PROBLEMS_PATH, {}, (_, problems) => {
    resolve(problems.map(problem => parseInt(problem.split('_')[0])));
  });
});

const getQuestionInfos = ids => new Promise(resolve => {
  info(`${ids.length} questions resolved\n`);
  info('Start regenerating...');
  const idExist = ids.reduce((pre, id) => {
    pre[id] = true;
    return pre;
  }, {});
  getQuestionsDetails().then(questions => {
    resolve(questions.filter(question => idExist[question.id]).reverse());
  });
});

const getQuestionsTableContent = questions => questions.reduce(([items, links], question) => {
  const { id, title, difficulty, slug } = question;
  const questionUrlNum = id * 2 - 1;
  const submissionUrlNum = id * 2;
  const dirName = `${formatId(id)}_${slug}`;

  return [
    [...items, `\n| ${id} | [${title}][${questionUrlNum}] | ${DIFFICULTY_MAP[difficulty]} | [${dirName}][${submissionUrlNum}] |`],
    [...links, `\n[${questionUrlNum}]: ${questionUrl(slug)}\n[${submissionUrlNum}]: https://github.com/MrHuxu/leetcode/blob/master/problems/${dirName}/index.js`]
  ];
}, [
  ['| Sequence | Title | Difficulty | Submission |\n| - | :- | :-: | :- |'],
  []
]);

regenerateReadme = questions => {
  const [tableItems, questionLinks] = getQuestionsTableContent(questions);

  const basicContent = readFileSync(README_BASE_PATH);
  const submissionContent = tableItems.join('') + '\n' + questionLinks.join('');
  writeFileSync(README_PATH, `${basicContent}\n${submissionContent}`);
  info('\nRegenerate README.md done!');
};

getSolvedQuestionIds().then(
  ids => getQuestionInfos(ids),
  err => Promise.reject(err)
).then(
  questions => regenerateReadme(questions),
  err => Promise.reject(err)
);

TreeWidget::TreeWidget(QWidget* parent, Adaptor* adaptor) : QWidget(parent), m_adaptor(adaptor) {
	QHBoxLayout* layout = new QHBoxLayout(this);
	layout->setContentsMargins(0, 0, 0, 0);

	m_tree = new QTreeWidget();
	m_tree->headerItem()->setHidden(true);
	m_tree->sortByColumn(0, Qt::AscendingOrder);
	m_tree->setSortingEnabled(true);
	layout->addWidget(m_tree);

	QTreeWidgetItem* root = new QTreeWidgetItem();
	root->setText(0, "/");
	root->setIcon(0, QIcon(":icons/network.png"));
	m_tree->addTopLevelItem(root);

	m_items.left.insert(std::make_pair(m_adaptor->graph().index(), root));

	m_signals.push_back(m_adaptor->graph().onAddNode([this](dependency_graph::NodeBase& node) { onAddNode(node); }));

	m_signals.push_back(
	    m_adaptor->graph().onRemoveNode([this](dependency_graph::NodeBase& node) { onRemoveNode(node); }));

	connect(adaptor, &Adaptor::currentNetworkChanged, this, &TreeWidget::onCurrentNetworkChanged);
	connect(m_tree, &QTreeWidget::itemSelectionChanged, this, &TreeWidget::onCurrentSelectionChanged);
}

TreeWidget::~TreeWidget() {
	for(auto& s : m_signals)
		s.disconnect();
}

void TreeWidget::onAddNode(dependency_graph::NodeBase& node) {
	assert(node.hasParentNetwork());

	auto parent = m_items.left.find(node.network().index());
	assert(parent != m_items.left.end());

	QTreeWidgetItem* item = new QTreeWidgetItem();
	item->setText(0, node.name().c_str());

	if(node.is<dependency_graph::Network>())
		item->setIcon(0, QIcon(":icons/network.png"));
	else
		item->setIcon(0, QIcon(":icons/node.png"));
	parent->second->addChild(item);

	m_items.left.insert(std::make_pair(node.index(), item));
}

void TreeWidget::onRemoveNode(dependency_graph::NodeBase& node) {
	auto it = m_items.left.find(node.index());
	assert(it != m_items.left.end());

	if(it->second != nullptr && it->second->parent() != nullptr)
		it->second->parent()->removeChild(it->second);
	delete it->second;
	m_items.left.erase(it);
}

void TreeWidget::onCurrentNetworkChanged(const dependency_graph::NodeBase& node) {
	auto it = m_items.left.find(node.index());
	assert(it != m_items.left.end());

	if(!it->second->isExpanded())
		it->second->setExpanded(true);
}

void TreeWidget::onSelectionChanged(const dependency_graph::Selection& selection) {
	bool originalBlock = m_tree->blockSignals(true);

	m_tree->clearSelection();

	for(auto& n : selection.nodes()) {
		auto it = m_items.left.find(n.get().index());
		assert(it != m_items.left.end());

		it->second->setSelected(true);
	}

	m_tree->blockSignals(originalBlock);
}

void TreeWidget::onCurrentSelectionChanged() {
	// build a selection
	dependency_graph::Selection selection;
	for(auto& s : m_tree->selectedItems()) {
		auto it = m_items.right.find(s);
		assert(it != m_items.right.end());
		dependency_graph::UniqueId id = it->second;

		auto ii = m_adaptor->index().find(id);
		if(ii != m_adaptor->index().end()) {
			dependency_graph::NodeBase* node = ii->second.graphNode;
			if(node) {
				selection.addNode(*node);

				if(node->hasParentNetwork())
					for(auto& con : node->network().connections())
						if(con.first.node().index() == node->index() || con.second.node().index() == node->index())
							selection.addConnection(con.first, con.second);
			}
		}
	}

	// and set this selection in the parent adaptor
	bool originalBlock = m_tree->blockSignals(true);
	m_adaptor->setSelection(selection);
	m_tree->blockSignals(originalBlock);
}

